tasks.command:
echo "/* Glass Logo Style */
.logo-glass {
    font-size: 2rem;
    background: linear-gradient(135deg, #ffffff 0%, rgba(255,255,255,0.5) 50%, rgba(255,255,255,0.1) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: drop-shadow(0 0 10px rgba(255, 123, 84, 0.5)) drop-shadow(0 0 20px rgba(46, 196, 243, 0.3));
    transition: all 0.5s ease;
}

.logo-glass:hover {
    filter: drop-shadow(0 0 20px rgba(255, 123, 84, 0.8)) drop-shadow(0 0 30px rgba(46, 196, 243, 0.6));
    transform: scale(1.05);
}

.text-glow {
    text-shadow: 0 0 20px rgba(255, 255, 255, 0.3);
}
" >> "ziply/assets/css/custom.css"
